////////////////////////////////////////////////////////////////////////////////
// Update
////////////////////////////////////////////////////////////////////////////////
#pragma region update

void TitleUpdate_b()
{
  UBYTE i, j, s;

  LYC_REG = 0;
  // _title_next();

  title_inter_i = 0;

  if (
      (joy & J_START && !(prev_joy & J_START)) ||
      (joy & J_A && !(prev_joy & J_A)))
  {

    // reset_game();
    // nextGameMode = INTRO;
    // fade_transition = TRUE;
    // fade_type = FADE_WHITE;

    // transition_to(SCENE_LOGO, FADE_BLACK);
    // transition_to(SCENE_LOGO, FADE_NONE);
    // transition_to(SCENE_LOGO, FADE_WHITE);

    _title_next();

    for (s = 0; s < 14; s++)
    {
      set_sprite_prop(s, 0x10);
    }
  }

  if ((time % 0xF) == 0)
  {
    // SCY_REG--;

    // Fade in "Press Start"
    if (title_timer == 3)
    {
      s = 0;

      // PRESS
      for (i = 0; i < 5; i++)
      {
        j = i * 2;
        if (i == 4)
        {
          j -= 2;
        }
        set_sprite_prop(s, 0x10);
        set_sprite_tile(s, j);
        move_sprite(s, 44 + 8 * i, 112);
        s++;
      }

      // START
      for (i = 0; i < 5; i++)
      {
        j = 6 + i * 2;
        if (i == 3)
        {
          j = 2;
        }
        else if (i == 4)
        {
          j = 8;
        }
        set_sprite_prop(s, 0x10);
        set_sprite_tile(s, j);
        move_sprite(s, 92 + 8 * i, 112);
        s++;
      }

      // 2019
      for (i = 0; i < 4; i++)
      {
        j = 12 + i * 2;
        set_sprite_prop(s, 0x10);
        set_sprite_tile(s, j);
        move_sprite(s, 72 + 8 * i, 136);
        s++;
      }

      OBP1_REG = 0xE7;
    }
    else if (title_timer == 4)
    {
      OBP1_REG = 0xD2;
    }

    if (title_timer < 4)
    {
      title_timer++;
    }
  }

  if (IS_FRAME_2)
  {
    title_bg_timer++;
  }
}

static void _title_next()
{
  if (title_exit)
  {
    return;
  }
  title_exit = TRUE;
  FadeSetSpeed(4);
  FadeOut();
  HIDE_SPRITES;

  stage_next_type = MAP;
}

